81.source:
function connectLeads(leadA, leadB) {
    var existing = this.connectionBetween(leadA, leadB);
    if (existing) {
        // remove a connection if it exists
        existing.remove();
    } else {
        // otherwise, create a new one
        var a = this.localize(leadA.globalBounds().center());
        var b = this.localize(leadB.globalBounds().center());
        var line = lively.morphic.Morph.makeLine([a, b], 4, Color.black);
        line.leadA = leadA;
        line.leadB = leadB;
        line.disableGrabbing();
        line.disableDropping();
        this.addMorphBack(line);
        line.getControlPoints().first().setConnectedMagnet(leadA.getMagnets().last());
        line.getControlPoints().last().setConnectedMagnet(leadB.getMagnets().last());
    }
    this.buildCircuit();
}

84.source:
function buildCircuit() {
    // create each Babelsberg component in this circuit
    this.allComponents().forEach(function(component){
       component.setupComponent();
    });
    // find disjoint sets of connected leads
    var setsOfLeads = this.createArrayOfSets();
    this.allConnections().forEach(function(connection){
        var setA = setsOfLeads.removeSetContaining(connection.leadA);
        var setB = setsOfLeads.removeSetContaining(connection.leadB);
        setsOfLeads.addSetJoining(setA, setB);
    });
    // connect leads in each set
    setsOfLeads.forEach(function(leadMorphs){
        var leads = leadMorphs.pluck("lead");
        users.ohshima.ElectricalComponents.Lead.connectAll(leads);
    });
    // update meters
    this.allComponents().forEach(function(component) {
       if (component.updateMeter) {
           component.updateMeter()
       }
    });
}

