function createArrayOfSets() {
    // make a helper object with the functions we need
    var arrayOfSets = [];
    arrayOfSets.removeSetContaining = function(item) {
        // find set with item, remove and answer set, otherwise make a new set
        var foundIndex = -1;
        for (var i = 0; i < arrayOfSets.length; i++)
            if (arrayOfSets[i].indexOf(item) !== -1) {
                foundIndex = i;
                break;
            }
        var setContainingItem;
        if (foundIndex >= 0) {
            // remove existing set
            setContainingItem = arrayOfSets[foundIndex];
            arrayOfSets.splice(foundIndex, 1);  
        } else {
            // or make new set
            setContainingItem = [item];
        }
        return setContainingItem;
    };
    arrayOfSets.addSetJoining = function(setA, setB) {
        // join setA and setB, add it to array
        for (var i = 0; i < setB.length; i++)
            if (setA.indexOf(setB[i]) === -1)
                setA.push(setB[i]);
        arrayOfSets.push(setA);
    }
    return arrayOfSets;
}

