function connectionBetween(leadA, leadB) {
    return this.allConnections().detect(function(each){
        return (each.leadA === leadA && each.leadB === leadB) ||
            (each.leadA === leadB && each.leadB === leadA);
    });
}

133.source:
function onMouseDown(evt) {
    if (evt.isLeftMouseButtonDown()) {
        evt.stop();
        this.setFill(Color.green);
        var self = this,
            pos = this.globalBounds().center(),
            wire = lively.morphic.Morph.makeLine([pos, pos], 4, Color.green);
        wire.openInWorld();
        var tracker = lively.morphic.Morph.makeEllipse(rect(-5,-5,10,10));
        tracker.openInHand();
        tracker.wantsToBeDroppedInto = function(target) {
            return !!target.connectElectricLead;
        }
        tracker.onDropOn = function(target) {
            tracker.remove();
            wire.remove();
            self.setFill(Color.gray);
            if (target.connectElectricLead)
                target.connectElectricLead(self);
        }
        tracker.step = function() {
            var pos = tracker.getPositionInWorld();
            wire.getControlPoint(1).setGlobalPos(pos);
        }
        tracker.startStepping(50, "step");
    }
}

