function removeUnecessaryIncoming() {
    this.itemList.each(function (ea) {
        if (ea.value > 0) {
            try {
                ea.nxt.prev = ea.prev;
            } catch (e) {
                // cannot remove ea, constraint violation
            }
        }
    })
}

