function updateLinks(item, nxt) {
    var prev;
    if (nxt) {
        prev = nxt.prev;
        item.nxt = nxt;
        nxt.prev = item;
    } else if (this.itemList.length > 1) {
        prev = this.itemList[this.itemList.length - 2];
    }
    item.prev = prev;
    if (prev) prev.nxt = item;
}

