function spliceItemIntoListAndReturnNext(item) {
    var nxt, idx = -1;
    try {
        this.itemList.each(function (ea, i) {
            if (ea.time <= item.time) { idx = i }
            else { nxt = ea; throw "break" };
        });
    } catch(e) {}
    this.itemList.splice(idx + 1, 0, item);
    return nxt;
}

