function constrainColor(color, prevColor, solver) {
    bbb.always({
        solver: solver,
        ctx: {
            solver: solver,
            color: color,
            prevColor: prevColor,
            _$_self: this.doitContext || this
        }
    }, function() {
        return color.getFill().isValid() && color.luminance == color.getFill().luminance() && color.hue == color.getFill().hue() && _$_self.luminance * .8 <= color.luminance && _$_self.hue * .8 <= color.hue;;
    });
    if (prevColor) {
        bbb.always({
            solver: solver,
            ctx: {
                solver: solver,
                color: color,
                prevColor: prevColor,
                _$_self: this.doitContext || this
            }
        }, function() {
            return color.lumSum == color.luminance + prevColor.lumSum && color.hueSum == color.hue + prevColor.hueSum;;
        });
    } else {
        bbb.always({
            solver: solver,
            ctx: {
                solver: solver,
                color: color,
                prevColor: prevColor,
                _$_self: this.doitContext || this
            }
        }, function() {
            return color.lumSum == color.luminance && color.hueSum == color.hue;;
        });
    }
}

100.source:
function constrainColor(color, prevColor, solver) {
    always: {solver: solver;
        color.getFill().isValid() &&
        color.luminance == color.getFill().luminance() &&
        color.hue == color.getFill().hue() &&
        this.luminance * 0.8 <= color.luminance &&
        this.hue * 0.8 <= color.hue
    };

    if (prevColor) {
        always: {solver: solver;
            color.lumSum == color.luminance + prevColor.lumSum &&
            color.hueSum == color.hue + prevColor.hueSum
        };
    } else {
        always: {solver: solver;
            color.lumSum == color.luminance &&
            color.hueSum == color.hue;
        };
    }
}

