function constrainColorProperties(px, pixelCount, solver) {
    bbb.always({
        solver: solver,
        ctx: {
            solver: solver,
            px: px,
            pixelCount: pixelCount,
            _$_self: this.doitContext || this
        }
    }, function() {
        return px.lumSum / pixelCount == _$_self.luminance && px.hueSum / pixelCount == _$_self.hue;;
    });
}

