function addConstraints() {
    this.reset();
    var charText = this.get("CharCountText");
    bbb.always({
        solver: this.cassowary,
        ctx: {
            ro: bbb.readonly,
            charText: charText,
            _$_self: this.doitContext || this
        }
    }, function() {
        return _$_self.remainingCharCount == ro(_$_self.maxLength) - ro(_$_self.currentLength);;
    });
    var self = this;
    bbb.always({
        solver: this.db,
        ctx: {
            ro: bbb.readonly,
            charText: charText,
            self: self,
            _$_self: this.doitContext || this
        }
    }, function() {
        return charText.getTextString().formula([ self.remainingCharCount ], function(v) {
            return v + "";
        });;
    });
}

