192.source:
function refresh() {
    var tree = this.get('ConstraintInspectorTree');
    var inspector = this;

    var selected = this.selected.bind(this);

    var morphItems = []; // tree items for all morphs with constraints

    this.withConstrainedMorphsDo(function(morph, variables) {
        var variable;
        var constraints;

        var varItems = [];
        var constraintItems;

        for (var key in variables) {
            variable = variables[key];
            constraints = variable._constraints;
            constraintItems = [];

            constraints.each(function(constraint) {
                constraintItems.push({
                    name: inspector.fmt(constraint),
                    data: constraint,
                    onSelect: selected
                });
            });

            varItems.push({
                name: key,
                description: this.typename(variables[key]),
                data: variables[key],
                children: constraintItems,
                onSelect: selected
            });
        }

        if (varItems.length > 0) {
            morphItems.push({
                name: morph.name,
                description: this.typename(morph),
                data: morph,
                children: varItems,
                onSelect: selected
            });
        }

        this.highlight(morph);
    }.bind(this));

    tree.setItem({
        name: 'Morphs with Constraints',
        children: morphItems
    });

    tree.expand();
}

