function selected(tree) {
    var item = tree.item;
    var data = item.data;

    if (!data) return false;

    if (data.isMorph) {
        this.highlight(data);
    } else if (typeof data === 'object' || typeof data === 'function') {
        inspect(data);
    }

    return true;
}

