function fmt(constraint) {
    var desc = constraint.predicate.source;

    desc = desc.replace(/;/g, '');
    desc = desc.replace(/\n/g, ' ');
    desc = desc.replace(/_\$_self/g, 'this');
    desc = desc.trim();
    desc = desc.replace(/^function[^\(]*\(\)\s*{(.*)}$/, '$1');
    desc = desc.trim();
    desc = desc.replace(/^return /, '');

    return desc;
}

