function resolve() {
    // Package version
    var Version = function Version(name, major, minor, patch) {
        this.name = name;
        this.major = major;
        this.minor = minor;
        this.patch = patch;
    };

    // Package repository
    //
    // Encapsulates information on available packages,
    // allows for searching package versions by name.
    var Repo = function Repo(content) {
        this._versions = JSON.parse(content).map(function (ver) {
            return new Version(ver.name, ver.major, ver.minor, ver.patch);
        });
    };

    Repo.prototype.search = function search(name) {
        return this._versions.filter(function (ver) {
            return ver.name == name;
        });
    };

    // Version specification
    //
    // Supported formats:
    //   - "= x.x.x"
    //   - ">= x.x.x"
    //   - "~> x.x.x"
    //   - "*"
    var Spec = function Spec(name, spec) {
        var parts = spec.split(/ |\./g);

        this.name  = name;
        this.type  = parts[0]; // =, >=, ~>, *
        this.major = parseInt(parts[1], 10);
        this.minor = parseInt(parts[2], 10);
        this.patch = parseInt(parts[3], 10);
    };

    // Predicate
    //
    // Allows for building complex compound predicates.
    //
    // Example:
    //   var and = new Predicate('&&', ['a >= b', 'b >= c']);
    //   var or = new Predicate('||', [and, 'd == 0']);
    //
    //   or.toString(); // => '((a >= b && b >= c) || d == 0)'
    //
    //   or.predicates.push('f == 12');
    //
    //   or.toString(); // => '((a >= b && b >= c) || d == 0 || f == 12)'
    var Predicate = function Predicate(type, predicates) {
        if (!predicates) {
            predicates = type;
            type = '&&';
        }

        this.predicates = predicates;
        this.type = type;
    };

    Predicate.prototype.toString = function toString() {
        return this.predicates.map(function (pred) {
            return '(' + pred.toString() + ')';
        }).join(' ' + this.type + ' ');
    };

    Predicate.prototype.toFunction = function toFunction() {
        return new Function('return (' + this.toString() + ');');
    };

    // Resolver for dependency specs
    //
    // Creates package constraints from a version specification
    // and tries to satisfy them with packages from a given package repository.
    var Resolver = function Resolver(repo) {
        this._repo = repo;
    };

    Resolver.prototype.resolve = function resolve(deps) {
        var repository = this._repo;
        var pkg = [];

        var solver = new ServerZ3(true);
        solver.reset();

        Object.keys(deps).each(function (name) {
            // Find available versions for the current package
            var versions = repository.search(name);

            // Read requested package version spec
            var spec = new Spec(name, deps[name]);

            // This object will hold the resolved version information
            var res = new Version(name, 0, 0, 0);

            // Add required constraints for the actual version specification
            var predicate; // constraint function, ctx: spec, res, ro

            if (spec.type == '>=') {
                predicate = new Predicate('||', [
                    'res.major >= ro(spec.major)',
                    'res.major == ro(spec.major) && spec.minor >= ro(spec.minor)',
                    'res.major == ro(spec.major) && spec.minor == ro(spec.minor) && res.patch >= ro(spec.patch)'
                ]);
            } else if (spec.type == '~>') {
                if (isNaN(spec.minor)) {
                    predicate = new Predicate([
                        'res.major >= ro(spec.major)'
                    ]);
                } else if (isNaN(spec.patch)) {
                    predicate = new Predicate('&&', [
                        'res.major == ro(spec.major)',
                        'res.minor >= ro(spec.minor)'
                    ]);
                } else {
                    predicate = new Predicate('&&', [
                        'res.major == ro(spec.major)',
                        'res.minor == ro(spec.minor)',
                        'res.patch >= ro(spec.patch)'
                    ]);
                }
            } else if (spec.type == '=') {
                predicate = new Predicate('&&', [
                    'res.major == ro(spec.major)',
                    'res.minor == ro(spec.minor)',
                    'res.patch == ro(spec.patch)'
                ]);
            } else if (spec.type == '*') {
                // here we do not create a predicate, as we would like to fetch
                // any available version to satisfy the version requirement
            } else {
                throw 'Version spec not implemented: ' + spec.type;
            }

            if (predicate) {
                solver.always({
                    ctx: {
                        res: res,
                        spec: spec,
                        ro: bbb.readonly
                    }
                }, predicate.toFunction());
            }

            // Add a compound constraint that the resolved version must
            // correspond to any of the available versions
            var disjunction = new Predicate('||', versions.map(function (ver) {
                return new Predicate('&&', [
                    'res.major == ' + ver.major,
                    'res.minor == ' + ver.minor,
                    'res.patch == ' + ver.patch
                ]);
            }));

            // console.log(req.name + ': ' + disjunction);

            try {
                solver.always({
                    ctx: {
                        res: res,
                        ro: bbb.readonly
                    }
                }, disjunction.toFunction());

                pkg.push(res); // push the resolved version
            } catch (e) {
                pkg.push({
                    name: spec.name + ' (unresolved)',
                    major: 'x',
                    minor: 'x',
                    patch: 'x'
                });
            }
        });

        return pkg;
    };


    // ---


    // Fetch the repository content
    var repository = new Repo(this.get('Repository').get('RepoPane').textString);

    // Read given package dependencies
    var input = this.get('CodePane');
    var json = JSON.parse(input.textString.replace(/\/\/.*\n/g, ''));
    var deps = json.dependencies;

    // Try to resolve the specified versions 
    var resolver = new Resolver(repository);
    var pkg = resolver.resolve(deps);

    // Output results

    var output = this.get('OutputText');

    output.textString = pkg.map(function (res) {
        return res.name + ': ' + [res.major, res.minor, res.patch].join('.');
    }).join('\n');
}

