function reset() {
    this.stopStepping();

    // ensure a clean slate
    this.submorphs.each(function (ea) {
        bbb.unconstrain(ea, "_Position");
        bbb.unconstrain(ea, "_FixedPosition");
        bbb.unconstrain(ea._Position, "x");
        bbb.unconstrain(ea._Position, "y");
        bbb.unconstrain(ea, "_Rotation");
        bbb.unconstrain(ea, "t");
        bbb.unconstrain(ea, "r");
    });

    // make sure we're centered
    this.setOrigin(pt(this.shape.getBounds().width / 2, this.shape.getBounds().height / 2));

    // setup references
    var pistons = [];
    this.submorphs.each(function (ea) {
        if (ea instanceof lively.morphic.Box) pistons.push(ea);
        if (ea.reset) ea.reset();
        ea.r = 0; ea.t = 0;
    });

    this.setupConstraints(pistons, this.get("Crank"));

    // this.step()
    this.startStepping(1, "step");
}

