function setupConstraints(pistons, crank) {
    var pistonStepSize = 2 * Math.PI / pistons.length;
    bbb.defaultSolver = new ServerZ3();
    bbb.defaultSolver.reset();
    pistons.concat([ crank ]).each(function(ea) {
        bbb.always({
            ctx: {
                Math: Math,
                ea: ea,
                _$_self: this.doitContext || this
            }
        }, function() {
            return Math.cos(ea.t) * ea.r == ea.getPosition().x && Math.sin(ea.t) * ea.r == ea.getPosition().y;;
        });
    }.bind(this));
    bbb.always({
        ctx: {
            Math: Math,
            pistons: pistons,
            bbb: bbb,
            ServerZ3: ServerZ3,
            crank: crank,
            pistonStepSize: pistonStepSize,
            _$_self: this.doitContext || this
        }
    }, function() {
        return crank.r == 40;;
    });
    pistons.each(function(ea, idx) {
        bbb.always({
            ctx: {
                ea: ea,
                pistonStepSize: pistonStepSize,
                idx: idx,
                crank: crank,
                Math: Math,
                _$_self: this.doitContext || this
            }
        }, function() {
            return ea.t == pistonStepSize * (idx + 1) && ea.getPosition().dist(crank.getPosition()) == 200;;
        });
        ea.setRotation(ea.t + .5 * Math.PI);
    }.bind(this));
}

