function setupConstraints(pistons, crank) {
    var pistonStepSize = 2 * Math.PI / pistons.length;
    bbb.defaultSolver = new ServerZ3();
    bbb.defaultSolver.reset();

    pistons.concat([crank]).each(function (ea) {
        always: { Math.cos(ea.t) * ea.r == ea.getPosition().x &&
                  Math.sin(ea.t) * ea.r == ea.getPosition().y }
    }.bind(this));

    always: { crank.r == 40 }

    pistons.each(function (ea, idx) {
        always: { ea.t == pistonStepSize * (idx + 1) &&
                  ea.getPosition().dist(crank.getPosition()) == 200; }
        ea.setRotation(ea.t + (0.5 * Math.PI));
    }.bind(this));
}

