function addConstraints() {
    var x = this.get("xSlider"), y = this.get("ySlider"), cassowary = new ClSimplexSolver();
    bbb.always({
        solver: cassowary,
        ctx: {
            ClSimplexSolver: ClSimplexSolver,
            cassowary: cassowary,
            y: y,
            x: x,
            _$_self: this.doitContext || this
        }
    }, function() {
        return y.getValue() <= 1;;
    });
    bbb.always({
        solver: cassowary,
        priority: "strong",
        ctx: {
            ClSimplexSolver: ClSimplexSolver,
            cassowary: cassowary,
            y: y,
            x: x,
            _$_self: this.doitContext || this
        }
    }, function() {
        return x.getValue() * 2 == y.getValue();;
    });
}

